import React from "react";
import { MapContainer, TileLayer, Marker } from "react-leaflet";
import L, { Layer } from "leaflet";
import LeafletPolyline from "react-leaflet-polyline";

function ResultsMap({ locations, guessLocation, round }) {
  const guessIcon = new L.Icon({
    iconUrl: require("../images/skycon.png"),
    iconAnchor: new L.Point(30, 60),
    iconSize: new L.Point(60, 60),
  });

  const answerIcon = new L.Icon({
    iconUrl: require("../images/housecon.png"),
    iconAnchor: new L.Point(30, 60),
    iconSize: new L.Point(60, 60),
  });

  return (
    <div className="results-map">
      <MapContainer
        maxBounds={[
          [-64, -180],
          [110, 156],
        ]}
        center={[40, 0]}
        zoom={1}
        scrollWheelZoom={true}
      >
        <TileLayer
          minZoom={1}
          maxZoom={8}
          noWrap={true}
          url="https://tiles.modmapper.com/{z}/{x}/{y}.jpg"
        >

<LeafletPolyline color={'black'} 
                 positions={[[locations[round].longitude, locations[round].latitude], guessLocation]}>balls</LeafletPolyline>

        </TileLayer>

        {locations && guessLocation ? (
          <>
            <Marker
              icon={answerIcon}
              position={[locations[round].longitude, locations[round].latitude]}
            ></Marker>

            <Marker icon={guessIcon} position={guessLocation}></Marker>
          </>
        ) : null}
      </MapContainer>
    </div>
  );
}

export default ResultsMap;
