import React from "react";

function ResultsInfo({ round, locations, guessLocation, onContinueClick, onNewGameClick }) {

  let button;
  button =
    round < 4 ? (
      <button onClick={onContinueClick}>Play Next Round</button>
    ) : (
      <button onClick={onNewGameClick}>Play New Game</button>
    );

  return (
    <div className="results-info">
      <p>this is the info area</p>
      <p>the round score will go here</p>
      <p>your guess was ___m/km from the correct location.</p>
      {button}
    </div>
  );
}

export default ResultsInfo;
