import "../css/index.css";
import Start from "./Start";
import Play from "./Play";
import Results from "./Results";
import { useState } from "react";

function App() {
  const [mode, setMode] = useState("start");
  const [round, setRound] = useState(0);
  return (
    <div className="App">
      Round: {round}
      {mode === "start" && (
        <Start
          onStartClick={() => {
            setMode("play");
            setRound(round + 1);
          }}
        ></Start>
      )}
      {mode === "play" && (
        <Play
          onRoundEnd={() => {
            setMode("results");
          }}
        ></Play>
      )}
      {mode === "results" && (
        <Results onContinueClick={() => setMode("play")}></Results>
      )}
    </div>
  );
}

export default App;
