import React from "react";
import { useState, useEffect } from "react";

function ResultsInfo({
  round,
  locations,
  guessLocation,
  onContinueClick,
  onNewGameClick,
}) {

    const [distance, setDistance] = useState(0);

    useEffect(() => {
        console.log(guessLocation);
        let x1 = locations[round].latitude;
        let y1 = locations[round].longitude;

        let x2 = guessLocation.lat;
        let y2 = guessLocation.lng;


        setDistance(calcDistance(x1, x2, y1, y2));

    }, []);

  const calcDistance = (x1, x2, y1, y2) => {
    return Math.rouMath.sqrt( ((x2 - x1) ** 2) + ((y2 - y1) ** 2) ) / 10;
  };

  let button;
  button =
    round < 4 ? (
      <button onClick={onContinueClick}>Play Next Round</button>
    ) : (
      <button onClick={onNewGameClick}>Play New Game</button>
    );

  return (
    <div className="results-info">
      <p>this is the info area</p>
      <p>the round score will go here</p>

        {distance}
 
      {button}
    </div>
  );
}

export default ResultsInfo;
