import React from "react";
import { useState, useEffect } from "react";

function ResultsInfo({
  round,
  locations,
  guessLocation,
  onContinueClick,
  onNewGameClick,
}) {

    const [distance, setDistance] = useState(null);

    useEffect

  const getDistance = (x1, x2, y1, y2) => {
    return Math.sqrt((x2 - x1) ** 2 + (y2 - y1) ** 2);
  };

  let button;
  button =
    round < 4 ? (
      <button onClick={onContinueClick}>Play Next Round</button>
    ) : (
      <button onClick={onNewGameClick}>Play New Game</button>
    );

  return (
    <div className="results-info">
      <p>this is the info area</p>
      <p>the round score will go here</p>
      <p>
        your guess was {distance(locations[round].latitude)}m/km from the
        correct location.
      </p>
      {button}
    </div>
  );
}

export default ResultsInfo;
