import { useMapEvents } from "react-leaflet";

function ResultsMapEvents({locations, guessLocation, round}) {
  const map = useMapEvents({
    add: () => {
      map.fitBounds([[locations[round].latitude, locations[round].longitude], guessLocation], 6)
    }
  });
}

export default ResultsMapEvents;
